import { useEffect } from 'react'
import blogServices from '../services/blogs'
const BlogInfo = ({ blog }) => {
    const like = async (e) => {
        e.preventDefault()
        console.log(blog)
        const blogToUpdate = {...blog, likes: blog.likes + 1, user: blog.user.id}
        console.log('blog with likes',blogToUpdate)
        const updatedBlog = await blogServices.update(blog.id, blogToUpdate)
        console.log(updatedBlog)
        return updatedBlog
    }

    return(
        <>
            <em>Url: {blog.url}</em><br/>
            <em>Likes: {blog.likes}</em><button onClick={like}>like me!</button><br/>
            <em>Author: {blog.author}</em><br/>
        </>
    )
}
export default BlogInfo
