import { useState } from 'react'

const BlogForm = ({ add }) => {
	const [title, setTitle] = useState('')
	const [author, setAuthor] = useState('')
	const [url, setUrl] = useState('')

	const addBlog = (e) => {
		e.preventDefault()
		const blogObject = {
			title: title,
			author: author,
			url: url,
		}
		add(blogObject)
		setAuthor('')
		setTitle('')
		setUrl('')
	}

	return(
		<div>
			<form onSubmit={addBlog}>
				<div>
					<label>title: </label><input type='text' placeholder='title' value={title} onChange={({ target }) => setTitle(target.value)} />
				</div>
				<div>
					<label>author: </label><input type='text' placeholder='autho' value={author} onChange={({ target }) => setAuthor(target.value)} />
				</div>
				<div>
					<label>url: </label><input type='text' value={url} onChange={({ target }) => setUrl(target.value)} />
				</div>
				<button type="Submit">create</button>
			</form>
		</div>
	)
}
export default BlogForm
