import { useState, forwardRef ,useImperativeHandle } from 'react'

const Toggable = forwardRef((props, refs) => {
    const [ visable, setVisable] = useState(false)

    const hide = {display: visable ? 'none' : ''}
    const show = {display: visable ? '' : 'none'}

    const toogleVisibility = () => setVisable(!visable)

    useImperativeHandle(refs, () => {
        return {toogleVisibility}
    })
    return(
        <div>
            <div style={hide}>
                <button onClick={toogleVisibility}>{props.buttonLabel}</button>
            </div>
            <div style={show}>
                {props.children}
                <button onClick={toogleVisibility}>cancel</button>
            </div>
        </div>
    )
})
export default Toggable
