const handleLogin = async (event) => {
    event.preventDefault()
    try {
        const user = await loginService.login({username, password,})
        window.localStorage.setItem('loggedNoteAppUser', JSON.stringify(user))
        blogsService.setToken(user.token)
        setUser(user)
        setUsername('')
        setPassword('')
    }
    catch (exception) {
      console.log(exception)
    }
  }

const loginForm = (params) => {
    return (
        <div>
            <h2>Log-in to application</h2>
            <form onSubmit={handleLogin}>
                <div>
                    <label>username: </label><input type='text' name='Username' value={params.username} onChange={({target}) => params.setUsername(target.value)} />
                </div><br/>
                <div>
                    <label>password: </label><input type="password" value={params.password} name="Password" onChange={({target}) => params.setPassword(target.value)} />
                </div>
                <button type='submit'>login</button>
            </form>
        </div>
    )
}
export default loginForm
