import { useState } from "react"

const Blog = ({ blog }) => {
    const [click, setClick] = useState()
    return (
        <p>
            <li>{blog.title}</li>
            <button onClick={() => setClick(!click)}>{click ? 'show': 'hide'}</button>
            {click ? <BlogInfo blog={blog} /> : ''}
        </p>
    )
}
export default Blog
