import { useState } from "react"
import BlogInfo from "./BlogInfo"

const Blog = ({ blog  }) => {
    const [click, setClick] = useState()
    return (
        <p>
            <li>{blog.title}
            <button onClick={() => setClick(!click)}>{click ? 'hide': 'show'}</button>
            </li>
            {click ? <BlogInfo blog={blog} /> : ''}
        </p>
    )
}
export default Blog
