import { useState, useEffect } from 'react'

import blogsService from './services/blogs'
import loginService from './services/login'

import LoginForm from './components/LoginForm'
import BlogForm from './components/BlogForm'
import Blogs from './components/Blogs'
import Notification from './components/Notification'
im


const App = () => {
  const [blogs, setBlogs] = useState([])

  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [url, setUrl] = useState('')

  const [message, setMessage] = useState('')
  const[className, setClassName] = useState('')

  const [user,setUser] = useState(null)

  useEffect(() => {
     blogsService
      .getAll()
      .then(currentBlogs => setBlogs(currentBlogs))
  }, [])
  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogAppUser')
    if(loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogsService.setToken(user.token)
    }
  },[])

  const handleLogout = async (event) => {
    try {
      event.preventDefault()
      window.localStorage.removeItem('loggedBlogAppUser')
      setUser(null)
      setClassName('success')
      setMessage(`User successfully logged out`)
      setTimeout(() => setMessage(null), 5000)
      setTimeout(() => setClassName(null), 5000)
    } catch (exception) {
      console.log(exception)
      setClassName('fail')
      setMessage(`something went wrong`)
      setTimeout(() => setMessage(null), 5000)
      setTimeout(() => setClassName(null), 5000)
    }
  }

  const handleLogin = async (username, password) => {
    try {
        const user = await loginService.login({username, password,})
        window.localStorage.setItem('loggedBlogAppUser', JSON.stringify(user))
        blogsService.setToken(user.token)
        setUser(user)
        setClassName('success')
        setMessage(`User ${username} successfully logged in`)
        setTimeout(() => setMessage(null), 5000)
        setTimeout(() => setClassName(null), 5000)
    }
    catch (exception) {
      console.log(exception)
      setClassName('fail')
      setMessage(`wrong username or pasword`)
      setTimeout(() => setMessage(null), 5000)
      setTimeout(() => setClassName(null), 5000)
    }
  }
  const handleBlogAdd = async(e) => {
    e.preventDefault()
    try {
      const blogObject = {
        title: title,
        author: author,
        url: url,
      }
      const newBlog = await blogsService.create(blogObject)
      setBlogs(blogs.concat(newBlog))
      setAuthor('')
      setTitle('')
      setUrl('')
      setClassName('success')
      setMessage(`A new blog:  ${blogObject.title}, by ${blogObject.author} was added`)
      setTimeout(() => setMessage(null), 5000)
      setTimeout(() => setClassName(null), 5000)
    } catch (exception) {
      console.log(exception)
      setClassName('fail')
      setMessage(`All data required!`)
      setTimeout(() => setMessage(null), 5000)
      setTimeout(() => setClassName(null), 5000)
    }
  }

  return (
    <div>
      {message ==='' ? null :<Notification message={message} className={className}/>}
      {user === null
        ? <LoginForm token={handleLogin} />
        : <div>
            <p><b>{user.name}</b> logged-in <button onClick={handleLogout}>Logout</button></p>
            <BlogForm addBlog={handleBlogAdd} title={title} author={author} url={url} setAuthor={setAuthor} setTitle={setTitle} setUrl={setUrl} />
          </div>
      }
      <Blogs blogs={blogs} />
    </div>
  );
}

export default App;
